import "smart-webcomponents-react/source/styles/smart.default.css";
import React, { Fragment, useEffect, useState } from "react";
import { Tank } from "smart-webcomponents-react/tank";
import classes from './WaterTank.module.css'
const WaterTank = (props) => {
  const containerMaxValue = 3000;

  const [waterExceed, setwaterExceed] = useState(false);
  const [waterAmount, setWaterAmount] = useState(0);

  const changeWaterAmountHandler = (event) => {



    useEffect(()=>{
setWaterAmount(0)
    },[props.restTank])
    const amount = Number(event.detail.value).toFixed(2)
    if(amount >= 2000){
         setwaterExceed(true)      
    }
    else{
        setwaterExceed(false)
    }
    setWaterAmount(amount);
    props.onSaveWaterAmount(amount);
  };
 
  return (
    <Fragment>
      <h2>Amount (milliliters)</h2>
      {waterExceed &&  <Fragment> <Tank
      id="tank"
      max="3000"
      value={waterAmount}
      onChange={changeWaterAmountHandler}
      interval="10"
    ></Tank>
    <p className={classes.exceed}>Watch out you exceed 2000 milliliters !!</p> </Fragment>
    }

    {!waterExceed && <Tank
      id="tank"
      max="3000"
      value={waterAmount}
      onChange={changeWaterAmountHandler}
      interval="10"
    ></Tank>}
    </Fragment>
  );
};

export default WaterTank;
