import Input from "../../UI/Input/Input";
import WaterTank from "./WaterTank";
import classes from "./WaterTrackerForm.module.css";
import React,{useState,useRef} from 'react'

//send data to api 
const WaterTrackerForm = (props) => {


     const nameInputRef  = useRef()
     const [resetTank,setResetTank] = useState(false)
    const userInput ={
        name:nameInputRef,
        amount:0
    }

   const  onSaveWaterAmountHandler = (enteredUserAmount) =>{
userInput.amount = enteredUserAmount.toFixed(2)
   }

const submitFormHandler =()=>{

}

const onResetHandler = ()=>{
    nameInputRef =''
    setResetTank(true)
}



   
  return (
    <form>
      <div className={classes["new-water-tracker__controls"]}>
        <div className={classes["new-water-tracker__control"]}>
          <label>Name</label>
          <Input input={{type:"text"}} ref={nameInputRef}/>
        </div>
        <div className={classes["new-water-tracker__control"]}>
          <WaterTank onSaveWaterAmount={onSaveWaterAmountHandler} onResetTank={resetTank}/>
        </div>
        <div className={classes["new-water-tracker__actions"]}>
          <div className={classes["new-water-tracker__action"]}>
            <button type="submit" onSubmit={submitFormHandler}>Add</button>
          </div>
          <div className={classes["new-water-tracker__action"]}>
            <button onClick={onResetHandler}>Reset</button>
          </div>
        </div>
      </div>
    </form>
  );
};

export default WaterTrackerForm;
