import "smart-webcomponents-react/source/styles/smart.default.css";
import React, { Fragment, useState, useEffect } from "react";
import { Tank } from "smart-webcomponents-react/tank";

const WaterTank = (props) => {

  const containerMaxValue = 3000;
  let content = (
    <Tank
      id="tank"
      max="3000"
      value={waterAmount}
      onChange={changeWaterAmountHandler}
      interval="10"
    ></Tank>
  );

  const [waterAmount, setWaterAmount] = useState("");
  const changeWaterAmountHandler = (event) => {
    console.log(event);
    setWaterAmount(event.detail.value);
    props.onSaveWaterAmount(waterAmount);
    if (Number(waterAmount) >= 2000) {
      console.log("here");
      content = (
        <Tank
          id="tank"
          max="3000"
          value={waterAmount}
          onChange={changeWaterAmountHandler}
          interval="10"
          className={classes[".smart-tank .smart-value"]}
        ></Tank>
      );
    } else {
      content = (
        <Tank
          id="tank"
          max="3000"
          value={waterAmount}
          onChange={changeWaterAmountHandler}
          interval="10"
        ></Tank>
      );
    }
  };
  const onResetHandler =()=>{
    setWaterAmount('');
  }
  return (
    <Fragment>
      <h2>Amount (milliliters)</h2>
      {content}
    </Fragment>
  );
};

export default WaterTank;
