import Input from "../../UI/Input/Input";
import WaterTank from "./WaterTank";
import classes from "./WaterTrackerForm.module.css";
import React, { useState, useRef } from "react";

//send data to api
const WaterTrackerForm = (props) => {
  const nameInput = useRef();
  const amountInput =0;
//   const userInput = {
//     name: nameInput,
//     amount: 0,
//   };

  const onSaveWaterAmountHandler = (enteredUserAmount) => {
    amountInput = Number(enteredUserAmount).toFixed(2);
  };

  const submitFormHandler = (event) => {
    event.preventDefault();
    const userInput ={
        name:nameInput.current.value,
        amount:amountInput
    }
    props.addNewWaterTrackerRecord(userInput);
  };

  const nameInputChangehandler = (event) => {
    setnameInput(event.target.value);
  };

  return (
    React.forwardRef(<form onSubmit={submitFormHandler}>
      <div className={classes["new-water-tracker__controls"]}>
        <div className={classes["new-water-tracker__control"]}>
          <label>Name</label>
          <Input input={{ type: "text" }} ref={nameInput} />
        </div>
        <div className={classes["new-water-tracker__control"]}>
          <WaterTank onSaveWaterAmount={onSaveWaterAmountHandler} />
        </div>
        <div className={classes["new-water-tracker__actions"]}>
          <div className={classes["new-water-tracker__action"]}>
            <button type="submit">
              Submit
            </button>
          </div>
          <div className={classes["new-water-tracker__action"]}>
            <button onClick={props.onResetTank}>Reset</button>
          </div>
        </div>
      </div>
    </form>)
  );
};

export default WaterTrackerForm;
