import "smart-webcomponents-react/source/styles/smart.default.css";
import React, { Fragment, useState, useRef } from "react";
import { Tank } from "smart-webcomponents-react/tank";
import classes from './WaterTank.module.css'
const WaterTank = (props) => {
  const containerMaxValue = 3000;

  const [waterExceed, setwaterExceed] = useState(false);
  const [waterAmount, setWaterAmount] = useState(0);

  const color = useRef();
  const changeWaterAmountHandler = (event) => {
    
    if(Number(event.detail.value).toFixed(2) >= 2000){
        console.log("here")
         setwaterExceed(true)
         const el = document.getElementById({color}).style.setProperty('--smart-color', 'red');
    }
    else{
        setwaterExceed(false)
    }
    setWaterAmount(event.detail.value);
    props.onSaveWaterAmount(waterAmount);
  };
  const onResetHandler = () => {
    setWaterAmount(0);
  };
  return (
    <Fragment>
      <h2>Amount (milliliters)</h2>
      {waterExceed &&  <Tank
      id="tank"
      max="3000"
      value={waterAmount}
      onChange={changeWaterAmountHandler}
      interval="10"
      ref={color}
      className={classes['smart-value']}
    ></Tank>}

    {!waterExceed && <Tank
      id="tank"
      max="3000"
      value={waterAmount}
      onChange={changeWaterAmountHandler}
      interval="10"
    ></Tank>}
     
    </Fragment>
  );
};

export default WaterTank;
