import { useEffect, useState } from "react";
import classes from "./Users.module.css";
import useHttp from "../../hooks/use-http";
import Modal from "../../Layout/Modal";
const usersData = [];
const users = () => {
  const [usersData, setUsersData] = useState([]);
  const trasformedUsers = (usersData) => {
    const loadedUsers = [];

    for (const user in usersData) {
      loadedUsers.push({
        id: user.id,
        name: user.name,
        username: user.username,
        email: user.email,
      });
    }
    setUsersData(loadedUsers);
  };
  const {
    isLoading,
    error,
    sendRequest: fetchUsers,
  } = useHttp(
    { url: "https://jsonplaceholder.typicode.com/users" },
    trasformedUsers
  );

  useEffect(() => {
    fetchUsers();
  }, []);
  let content;
  if(isLoading) {
    content = <p>Loading !!</p>
  }
  else if(!isLoading && error){
content =<p>{error}</p>
  }
  else{
    content =<Modal onClose={props.onClose}><table>
    <tr>
      <th>Id</th>
      <th>Name</th>
      <th>User Name</th>
      <th>Email</th>
    </tr>
    {usersData.map((user) => {
      return (
        <tr>
          <td>{user.id}</td>
          <td>{user.name}</td>
          <td>{user.username}</td>
          <td>{user.email}</td>
        </tr>
      );
    })}
  </table>
  <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onClose}>
          {" "}
          Close
        </button>
        </div></Modal>
  }
  return (
   {content}
  );
};

export default users;
