import "smart-webcomponents-react/source/styles/smart.default.css";
import React, { Fragment, useState, useEffect } from "react";
import { Tank } from "smart-webcomponents-react/tank";
import classes from './WaterTank.module.css'
const WaterTank = (props) => {
  const containerMaxValue = 3000;
  let setColor;

  const [waterAmount, setWaterAmount] = useState(0);
  const changeWaterAmountHandler = (event) => {
    setWaterAmount(event.detail.value);
    props.onSaveWaterAmount(waterAmount);
    if(Number(enteredUserAmount).toFixed(2) >= 2000){
         setColor = classes['.smart-tank .smart-value']
    }
  };
  const onResetHandler = () => {
    setWaterAmount(0);
  };
  return (
    <Fragment>
      <h2>Amount (milliliters)</h2>
      <Tank
      id="tank"
      max="3000"
      value={waterAmount}
      onChange={changeWaterAmountHandler}
      interval="10"
      className={setColor}
    ></Tank>
    </Fragment>
  );
};

export default WaterTank;
